@import '~@theme/styles/config.styl';

.footer {
  position: relative;
  overflow: hidden;
}

.footer-box__shape {
  position: absolute;
  bottom: 0;
  left: 0;
}

.footer-box1 {
  height: 175px;
  top: 4px;

  .footer-box__shape {
    fill: $firstFooterColor;
  }
}

.footer-box2 {
  height: 250px;
  background-color: $firstFooterColor;
  bottom: -2px;

  .footer-box__shape {
    fill: $secondFooterColor;
  }
}

.footer-box3 {
  position: relative;
  background-color: $secondFooterColor;
  padding: 50px 0;
}

.footer-box1, .footer-box2 {
  position: relative;
}

.footer-social {
  &__list {
    display: inline-block;
    margin-top: 8%;
    margin-left: 30px;
    z-index: 2;
    position: relative;
  }

  &__item {
    display: inline;
    margin-right: 16px;
    color: white;
  }

  &__icon {
    color: white;
    font-size: $title1;
  }
}

.footer-newsletter {
  position: relative;
  width: 40%;
  float: right;
  margin-top: -10px;
  margin-right: 12%;
  z-index: 10;

  .newsletter, .newsletter .meta-text {
    color: white;
  }
}

.footer-card {
  position: relative;
}

.footer {
  &__card-author {
    position: relative;
    z-index: 6;
    margin-left: 8px;
    margin-right: 8px;
  }
}

.watermark-logo {
  position: absolute;
  top: -150px;
  left: -30px;
  width: 472px;
  height: 429px;
  background-image: url('/watermark-logo.png');
  background-size: cover;
  background-repeat: no-repeat;
  z-index: 5;
}

.footer-nav {
  padding-right: 4vw !important;
  z-index: 6;

  &__box {
    text-align: right;
  }

  .title {
    color: white;
    font-weight: bold;
    font-size: $mediumText;
  }
}

.footer-nav1, .footer-nav2 {
  margin-top: 10px;

  &__item {
    font-size: $smallText;
    color: white;

    a {
      float: right;
      display: block;
      width: 85%;
      padding: 6px 4px;
      margin-bottom: 3px;

      &:hover {
        text-decoration: underline;
        color: white;
      }
    }
  }
}

.footer-copy {
  color: white;
  font-size: 11px;
  text-align: right;
  padding-top: 50px;
  padding-right: 4vw !important;

  a:hover {
    color: #3eaf7c;
  }
}

@media (max-width: $mobile) {
  .footer-box2 {
    height: 110px;
  }

  .footer-card {
    order: 2;
    margin: 40px 0;
  }

  .footer-copy {
    order: 3;
    text-align: center;
    padding-top: 10px;
  }

  .footer-nav {
    order: 1;
  }

  .footer-nav__box--1.column {
    text-align: left;
    padding-left: 5vw !important;
  }

  .footer-nav__box--2.column {
    padding-right: 5vw !important;
  }

  .footer-nav1__item a {
    float: left;
  }

  .footer-social__list {
    position: relative;
    left: 50%;
    margin-top: 5%;
    transform: translateX(-50%);
  }

  .footer-newsletter {
    display: none;
  }
}

@media (max-width: $max-tablet) {
  .footer-newsletter {
    width: 50%;
    margin-right: 8%;
  }
}

@media (min-width: $min-xlarge) {
  .footer-box1 {
    height: 249px;
  }
}
