export default {
  name: 'TableContents',

  props: {
    headers: {
      type: Array,
      required: true
    }
  },

  methods: {
    scrollToHeader (selector) {
      const el = document.getElementById(selector)
      let i = window.scrollY;
      let int = setInterval(() => {
        window.requestAnimationFrame(() => {
          window.scrollTo({ top: i })
        })
        i += 50;
        if (i >= (el.offsetTop - 40)) clearInterval(int);
      }, 1)
    }
  }
}
