export default {
  name: 'UIButton',

  props: {
    type: {
      type: String,
      default: 'button'
    },
    color: String
  },

  computed: {
    getClass () {
      const classes = {}
      if (this.color) {
        classes[`ui-button--${this.color}`] = this.color
      }
      return classes
    }
  }
}
