import EventBus from '@theme/plugins/EventBus'

export default {
  name: 'Overlay',

  data () {
    return {
      show: false
    }
  },

  mounted () {
    EventBus.$on('show_overlay', data => {
      this.show = data
    })
    EventBus.$on('close_overlay', data => {
      this.show = data
    })
  },

  methods: {
    close () {
      this.show = false
      EventBus.$emit('close_overlay')
    }
  }
}
