@import '~@theme/styles/config.styl';

.header {
  &--search-open {
    position: relative;
    z-index: 20;
  }

  &__logo {
    text-align: center;
  }
}

.header-navigation.header-nav {
  position: relative;

  @media (max-width: $mobile) {
    display: none;
  }
}

.header-top {
  position: relative;
  padding: 30px 20px 24px;
  background-color: white;

  &__hamburguer {
    position: relative;
    transform: rotate(90deg);
  }

  &__toggle.switch-bw {
    position: absolute;
    bottom: -35px;
    right: 10px;
  }

  &__languages {
    position: absolute;
    top: 32px;
    left: 100px;

    @media (max-width: $mobile) {
      top: auto;
      bottom: -42px;
      left: 18px;
    }
  }
}

@media (max-width: $mobile) {
  .header-top {
    top: 0;
    left: 0;
    width: 100%;

    &__toggle.switch-bw {
      display: none;
    }
  }
}
