@import '~@theme/styles/config.styl';

.page-header-cat {
  margin-top: 14px;
}

.post-cover {
  width: 100%;
  height: 358px;
  margin-top: 30px;
  margin-bottom: 30px;
  background-color: lighten($primaryColor, 90%);

  @media (max-width: $max-tablet) {
    height: 192px;
  }

  &--video {
    height: 80vh;

    @media (max-width: $max-tablet) {
      height: 300px;
    }
  }

  &__image {
    width: 100%;
    height: 100%;
    -o-object-fit: cover;
    object-fit: cover;
  }
}

.post-share {
  position: relative;
  margin-bottom: 20px;

  &__item {
    display: inline-flex;

    > .twitter-share-button {
      position: relative !important;
      top: 4px;
    }
  }

  &:before {
    content: '';
    width: 150px;
    height: 2px;
    background-color: $primaryColor;
    position: absolute;
    bottom: 0;
    left: 0;

    @media (max-width: $mobile) {
      left: 50%;
      transform: translateX(-50%);
    }
  }

  .share {
    margin-top: 12px;
    padding-bottom: 30px;
  }
}

.post-demo-button {
  text-align: right;

  .icon {
    top: -1px;
    margin-left: 6px;
  }
}

@media (max-width: $mobile) {
  .post-share {
    order: 2;
  }

  .post-demo-button {
    order: 1;
    margin-bottom: 30px;
  }

  .post-share, .post-demo-button {
    text-align: center;
  }
}

.post-content {
  width: calc(100% - 100px);

  &__box-player {
    overflow: hidden;
  }

  &__player {
    position: relative;
    width: 100%;
    margin-top: 30px;
    margin-bottom: 30px;
  }

  &__table-contents {
    margin-top: 60px;
    margin-bottom: 80px;
  }

  &__disqus {
    margin-top: 60px;
  }

  &__excerpt > .page-header__subtitle {
    font-size: 21px;
    line-height: 1.58em;
  }
}

.post-sidebar {
  &__bizu {
    margin-top: 40px;
    float: right;
    width: 100%;
    max-width: 180px;

    @media (max-width: $min-large) {
      word-break: break-all;
    }
  }
}
